import React, { useState } from 'react';

import Card from './components/card';
import Header from './components/header';
import DungeonsComp from './components/dungeons';
import TilesComp from './components/tiles';
import RaidsComp from './components/raids';
import FishingComp from './components/fishing';
import MonstersComp from './components/monsters'; 

import './css/App.css';


function App() {
  const [data, setData] = useState(null);
  const [info, setInfo] = useState(true);
  const [Dungeons, setDungeons] = useState(false);
  const [Tiles, setTiles] = useState(false);
  const [Raids, setRaids] = useState(false);
  const [Fishing, setFishing] = useState(false);
  const [Monsters, setMonsters] = useState(false);


  // React.useEffect(() => {
  //   fetch("/api")
  //     .then((res) => res.json())
  //     .then((data) => setData(data.message));
  // }, []);

  const handleClick = (tab) => {

    if (tab === "info"){
      setInfo(true);
    } else {
      setInfo(false);
    }

    if (tab === "Dungeons"){
      setDungeons(true);
    } else {
      setDungeons(false);
    }

    if (tab === "Tiles"){
      setTiles(true);
    } else {
      setTiles(false);
    }

    if (tab === "Raids"){
      setRaids(true);
    } else {
      setRaids(false);
    }

    if (tab === "Fishing"){
      setFishing(true);
    } else {
      setFishing(false);
    }

    if (tab === "Monsters"){
      setMonsters(true);
    } else {
      setMonsters(false);
    }

  }

  console.log(Monsters)

  return (
    <div className="app">
      <Header click={handleClick} />
      <div className="app__body">
      {info && <div className="app__info">
      <Card 
        title="Dungeons" 
        info="This section contains information on the overall mechanics for each dungeon, as well as for individual classes" 
      />
      <Card 
        title="Tile Sets" 
        info="This section contains information on tiles for each class" 
      />
      <Card 
        title="Raids" 
        info="This section contains information on the overall mechanics for the raid boss" 
      />
      <Card 
        title="Fishing" 
        info="This section contains information on fishing lures and the location of the different types of fish" 
      />
      <Card 
        title="Monsters" 
        info="This section contains information on each of the monsters in throughout the world. What they look like and any known mechanics" 
      />
      </div>}
      {Dungeons && <DungeonsComp />}
      {Tiles && <TilesComp />}
      {Raids && <RaidsComp />}
      {Fishing && <FishingComp />}
      {Monsters && <MonstersComp />}

      {}
      </div>

      <div className="app__footer"></div>
    </div>
  );
}

export default App;
