import React, { useState } from 'react';

import Header from './components/header';
import Home from './components/home';
import DungeonsComp from './components/dungeons';
import TilesComp from './components/tiles';
import RaidsComp from './components/raids';
import FishingComp from './components/fishing';
import MonstersComp from './components/monsters'; 

import './css/App.css';


function App() {
  // const [data, setData] = useState(null);
  const [info, setInfo] = useState(true);
  const [Dungeons, setDungeons] = useState(false);
  const [Tiles, setTiles] = useState(false);
  const [Raids, setRaids] = useState(false);
  const [Fishing, setFishing] = useState(false);
  const [Monsters, setMonsters] = useState(false);
  const [Sources, setSources] = useState(false);


  // React.useEffect(() => {
  //   fetch("/api")
  //     .then((res) => res.json())
  //     .then((data) => setData(data.message));
  // }, []);

  const handleClick = (tab) => {

    if (tab === "info"){
      setInfo(true);
    } else {
      setInfo(false);
    }

    if (tab === "Dungeons"){
      setDungeons(true);
    } else {
      setDungeons(false);
    }

    if (tab === "Tiles"){
      setTiles(true);
    } else {
      setTiles(false);
    }

    if (tab === "Raids"){
      setRaids(true);
    } else {
      setRaids(false);
    }

    if (tab === "Fishing"){
      setFishing(true);
    } else {
      setFishing(false);
    }

    if (tab === "Monsters"){
      setMonsters(true);
    } else {
      setMonsters(false);
    }

  }

  console.log(Monsters)

  return (
    <div className="app">
      <Header click={handleClick} />
      <div className="app__body">
      {info && <Home />}
      {Dungeons && <DungeonsComp />}
      {Tiles && <TilesComp />}
      {Raids && <RaidsComp />}
      {Fishing && <FishingComp />}
      {Monsters && <MonstersComp />}

      {}
      </div>

      <div className="app__footer"></div>
    </div>
  );
}

export default App;
