import React, { useState } from 'react';

import Card from './components/card';
import Header from './components/header';
import Monsters from './components/monsters';

import './css/App.css';


function App() {
  const [data, setData] = useState(null);
  const [info, setInfo] = useState(true);
  const [Dungeons, setDungeons] = useState(false);
  const [Tiles, setTiles] = useState(false);
  const [Raids, setRaids] = useState(false);
  const [Fishing, setFishing] = useState(false);
  const [Monsters, setMonsters] = useState(false);


  React.useEffect(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((data) => setData(data.message));
  }, []);

  const handleClick = (tab) => {
    const tabArray = [info, Dungeons, Tiles, Raids, Fishing, Monsters];
    const tabStateChangeArray = [setInfo, setDungeons, setTiles, setRaids, setFishing, setMonsters];
    let i = 0;
    const length = tabArray.length;

    for (i; i < length; ++i){
      if (tabArray[i] !== tab){
        console.log(false);
        tabStateChangeArray[i](false)
      } else {
        console.log(true);
        tabStateChangeArray[i](false)
      }
    }

  }

  return (
    <div className="app">
      <Header click={handleClick} />
      <div className="app__body">
      {info && <div className="app__info">
      <Card title="Dungeons" info="This section contains information on the overall mechanics for each dungeon, as well as for individual classes" />
      <Card title="Tile Sets" info="This section contains information on tiles for each class" />
      <Card title="Raids" info="This section contains information on the overall mechanics for the raid boss" />
      <Card title="Fishing" info="This section contains information on fishing lures and the location of the different types of fish" />
      <Card title="Monsters" info="This section contains information on each of the monsters in throughout the world. What they look like and any known mechanics" />
      </div>}
      </div>

      <div className="app__footer"></div>
    </div>
  );
}

export default App;
